/* eslint-disable */
import React, { useState, useEffect } from "react";
import {
  IonSlides,
  IonSlide,
  IonCardTitle,
  IonCard
} from "@ionic/react";

const SlideShow = () => {
  const [activeSlide, setActiveSlide] = useState(null);
  const slides = [
    {
      id: 0,
      name: "Zero",
      url: "https://i.ytimg.com/vi/PCwL3-hkKrg/maxresdefault.jpg"
    },
    {
      id: 1,
      name: "One",
      url: ""
    },
    {
      id: 2,
      name: "Two",
      url: ""
    },
    {
      id: 3,
      name: "Three",
      url: ""
    },
    {
      id: 4,
      name: "Four",
      url: ""
    }
  ];
  return (
    <IonSlides
      style={{ width: "100%", height: 200 }}
      pager={true}
      options={options}
    >
      {slides.map(({ id, url, name }) => (
        <IonSlide
          key={id}
          style={{ width: "100%", height: "100%", border: "1px gray dashed" }}
        >
          <IonCard>
            <IonCardTitle>{name}</IonCardTitle>
            <img src={url} />
          </IonCard>
        </IonSlide>
      ))}
    </IonSlides>
  );
};

export default SlideShow;

const options = {
  slidesPerView: 3,
  spaceBetween: 10,
  breakpoints: {
    320: {
      slidesPerView: 1,
      spaceBetween: 20
    },
    480: {
      slidesPerView: 1,
      spaceBetween: 30
    },
    640: {
      slidesPerView: 1,
      spaceBetween: 40
    }
  }
};
