/* eslint-disable */
import React from "react";
import {
  IonSlides,
  IonSlide,
  IonCardSubtitle
} from "@ionic/react";

const SlideShow = () => {
  const slides = [
    {
      id: 0,
      name: "Zero",
      url: "https://i.picsum.photos/id/1012/3973/2639.jpg"
    },
    {
      id: 1,
      name: "One",
      url: "https://i.picsum.photos/id/1015/6000/4000.jpg"
    },
    {
      id: 2,
      name: "Two",
      url: "https://i.picsum.photos/id/1020/4288/2848.jpg"
    },
    {
      id: 3,
      name: "Three",
      url: "https://i.picsum.photos/id/101/2621/1747.jpg"
    },
    {
      id: 4,
      name: "Four",
      url: "https://i.picsum.photos/id/1025/4951/3301.jpg"
    }
  ];
  return (
    <IonSlides
      style={{ width: "100%", height: 250 }}
      pager={true}
      options={options}
    >
      {slides.map(({ id, url, name }) => (
        <IonSlide
          key={id}
          style={{ width: "100%", height: 250}}
        >
          <div>
            <IonCardSubtitle>{name}</IonCardSubtitle>
            <img width="100%" height="200px" src={url} />
          </div>
        </IonSlide>
      ))}
    </IonSlides>
  );
};

export default SlideShow;

const options = {
  slidesPerView: 3,
  spaceBetween: 10,
  breakpoints: {
    320: {
      slidesPerView: 1,
      spaceBetween: 20
    },
    480: {
      slidesPerView: 1,
      spaceBetween: 30
    },
    640: {
      slidesPerView: 1,
      spaceBetween: 40
    }
  }
};
