import React, { useState, useEffect } from "react";
import {
  IonSlides,
  IonSlide,
  IonCardTitle,
  IonImg,
  IonCard
} from "@ionic/react";

const SlideShow = () => {
  const [activeSlide, setActiveSlide] = useState(null);
  const slides = [
    {
      id: 0,
      url: ""
    },
    {
      id: 1,
      url: ""
    },
    {
      id: 2,
      url: ""
    },
    {
      id: 3,
      url: ""
    },
    {
      id: 4,
      url: ""
    }
  ];
  useEffect(() => {
    console.log(activeSlide);
  }, [activeSlide]);
  return (
    <IonSlides
      style={{ width: "100%", height: 200 }}
      pager={true}
      options={options}
    >
      {slides.map(({ id, url }) => (
        <IonSlide
          key={id}
          style={{ width: "100%", height: "100%", border: "1px gray dashed" }}
        >
          <IonCard>
            <IonCardTitle>{id}</IonCardTitle>
            <img src={url} />
          </IonCard>
        </IonSlide>
      ))}
    </IonSlides>
  );
};

export default SlideShow;

const options = {
  // Default parameters
  slidesPerView: 1,
  spaceBetween: 10,
  // Responsive breakpoints
  breakpoints: {
    // when window width is >= 320px
    320: {
      slidesPerView: 1,
      spaceBetween: 20
    },
    // when window width is >= 480px
    480: {
      slidesPerView: 2,
      spaceBetween: 30
    },
    // when window width is >= 640px
    640: {
      slidesPerView: 2,
      spaceBetween: 40
    }
  }
};
