import React from "react";
import { IonSlides, IonSlide, IonCardSubtitle, IonButton } from "@ionic/react";

const SlideShow = () => {
  return (
    <>
      <IonSlides
        style={{ width: "100%", height: 250 }}
        pager={true}
        options={Fade}
      >
        {slides.map(({ id, url, name }) => (
          <IonSlide key={id} style={{ width: "100%", height: 250 }}>
            <div>
              <IonCardSubtitle>{name}</IonCardSubtitle>
              <img width="100%" height="200px" src={url} />
            </div>
          </IonSlide>
        ))}
      </IonSlides>
    </>
  );
};

export default SlideShow;

const slideOpts = {
  slidesPerView: 3,
  speed: 1000,
  autoplay: true,
  spaceBetween: 10,
  breakpoints: {
    320: {
      slidesPerView: 1,
      spaceBetween: 20
    },
    480: {
      slidesPerView: 1,
      spaceBetween: 30
    },
    640: {
      slidesPerView: 1,
      spaceBetween: 40
    }
  }
};

